request.description:
Send a message to user, group, or chatroom.               
Please refer to the Hyphenate docs for further details about message body. https://docs.hyphenate.io/docs/messages

{
	"target_type": "users", 		// users: messaging user to user. chatgroups: to groups. chatrooms: to chat room
	"target": [ 
		"apple"						// message receiver
	], 
	"msg": { 
		"type": "txt",				// message type. See details, https://docs.hyphenate.io/docs/messages
		"msg": "hello there!"		
	}, 
	"from": "orange",				// message sender
	"ext": {						// customer field for self-defined key-values
		"vote_count":"20", 
		"questionID":"q123" 
	}
}
